#!/bin/bash\n\necho \"🧹 Clearing all caches and restarting...\"\n\n# Stop any running development server\necho \"Stopping any running servers...\"\npkill -f \"npm start\" || true\npkill -f \"react-scripts start\" || true\n\ncd /Users/franky/Documents/code/labelme-full-stack/frontend\n\n# Clear npm cache\necho \"Clearing npm cache...\"\nnpm cache clean --force\n\n# Remove node_modules and reinstall\necho \"Reinstalling dependencies...\"\nrm -rf node_modules\nrm -f package-lock.json\nnpm install\n\necho \"✅ Cache cleared and dependencies reinstalled!\"\necho \"\"\necho \"🚀 Now:\"\necho \"   1. Close ALL Chrome tabs\"\necho \"   2. Restart Chrome completely\"\necho \"   3. Run: npm start\"\necho \"   4. Open in INCOGNITO mode\"\necho \"\"\necho \"📍 Or run: npm start\"\n